import React from "react";
import Row from './Row';

const loadData = (limit = 10, page = 1) => {
  const fetchReqest = async () => {
    return await fetch(`https://jsonplaceholder.typicode.com/posts?_limit=${limit}&page=${page}`).then(res => res.json).then(doc => doc);
  }
  return fetchReqest().catch(console.error)
}

export default function Playlist() {
  // TODO: Сделать получение выбранного цвета из контекста
  // TODO: При нажатии на элемент отправлять его Row в Player
  const [musicList, setMusicList] = React.useState([]);

  React.useEffect(() => {
    const newData = loadData();
    setMusicList(newData)
  }, [])
  return (
    <div className="List">
      {musicList.map(song => <Row song={song} />)}
    </div>
  )
}
